  1→'use client';
  2→
  3→import BlurFade from '@/components/magicui/blur-fade';
  4→import { Category } from '@/data/blog';
  5→import Link from 'next/link';
  6→import { useState } from 'react';
  7→
  8→const BLUR_FADE_DELAY = 0.04;
  9→const POSTS_PER_PAGE = 10;
 10→
 11→type Post = {
 12→  metadata: {
 13→    title: string;
 14→    publishedAt: string;
 15→    summary: string;
 16→    image?: string;
 17→    category?: Category;
 18→  };
 19→  slug: string;
 20→  source: string;
 21→};
 22→
 23→type CategoryInfo = {
 24→  key: Category | 'all';
 25→  label: string;
 26→};
 27→
 28→const categories: CategoryInfo[] = [
 29→  { key: 'all', label: 'All' },
 30→  { key: 'thoughts', label: 'Thoughts' },
 31→  { key: 'research', label: 'Research' },
 32→  { key: 'investing', label: 'Investing' },
 33→  { key: 'economics', label: 'Economics' },
 34→  { key: 'philosophy', label: 'Philosophy' }
 35→];
 36→
 37→export default function BlogClient({ posts }: { posts: Post[] }) {
 38→  const [selectedCategory, setSelectedCategory] = useState<Category | 'all'>(
 39→    'all'
 40→  );
 41→  const [currentPage, setCurrentPage] = useState(1);
 42→
 43→  const filteredPosts = posts
 44→    .filter((post) =>
 45→      selectedCategory === 'all'
 46→        ? true
 47→        : post.metadata.category === selectedCategory
 48→    )
 49→    .sort((a, b) => {
 50→      if (new Date(a.metadata.publishedAt) > new Date(b.metadata.publishedAt)) {
 51→        return -1;
 52→      }
 53→      return 1;
 54→    });
 55→
 56→  const totalPages = Math.ceil(filteredPosts.length / POSTS_PER_PAGE);
 57→  const startIndex = (currentPage - 1) * POSTS_PER_PAGE;
 58→  const paginatedPosts = filteredPosts.slice(
 59→    startIndex,
 60→    startIndex + POSTS_PER_PAGE
 61→  );
 62→
 63→  const handleCategoryChange = (category: Category | 'all') => {
 64→    setSelectedCategory(category);
 65→    setCurrentPage(1);
 66→  };
 67→
 68→  const handlePageChange = (page: number) => {
 69→    setCurrentPage(page);
 70→    window.scrollTo({ top: 0, behavior: 'smooth' });
 71→  };
 72→
 73→  const getPageNumbers = () => {
 74→    const pages: (number | 'ellipsis')[] = [];
 75→    if (totalPages <= 7) {
 76→      for (let i = 1; i <= totalPages; i++) {
 77→        pages.push(i);
 78→      }
 79→    } else {
 80→      pages.push(1);
 81→      if (currentPage > 3) {
 82→        pages.push('ellipsis');
 83→      }
 84→      const start = Math.max(2, currentPage - 1);
 85→      const end = Math.min(totalPages - 1, currentPage + 1);
 86→      for (let i = start; i <= end; i++) {
 87→        pages.push(i);
 88→      }
 89→      if (currentPage < totalPages - 2) {
 90→        pages.push('ellipsis');
 91→      }
 92→      pages.push(totalPages);
 93→    }
 94→    return pages;
 95→  };
 96→
 97→  return (
 98→    <section>
 99→      <BlurFade delay={BLUR_FADE_DELAY}>
100→        <h1 className='font-medium text-2xl mb-8 tracking-tighter'>blog</h1>
101→      </BlurFade>
102→
103→      <BlurFade delay={BLUR_FADE_DELAY * 1.5}>
104→        <div className='flex flex-wrap gap-2 mb-8'>
105→          {categories.map((category) => (
106→            <button
107→              key={category.key}
108→              onClick={() => handleCategoryChange(category.key)}
109→              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
110→                selectedCategory === category.key
111→                  ? 'bg-primary text-primary-foreground'
112→                  : 'bg-secondary text-secondary-foreground hover:bg-secondary/80'
113→              }`}>
114→              {category.label}
115→            </button>
116→          ))}
117→        </div>
118→      </BlurFade>
119→
120→      {paginatedPosts.map((post, id) => (
121→        <BlurFade delay={BLUR_FADE_DELAY * 2 + id * 0.05} key={post.slug}>
122→          <Link
123→            className='flex flex-col space-y-1 mb-4'
124→            href={`/blog/${post.slug}`}>
125→            <div className='w-full flex flex-col'>
126→              <div className='flex items-center gap-2'>
127→                <p className='tracking-tight'>{post.metadata.title}</p>
128→                {post.metadata.category && (
129→                  <span className='text-xs px-2 py-0.5 rounded bg-secondary text-secondary-foreground'>
130→                    {
131→                      categories.find((c) => c.key === post.metadata.category)
132→                        ?.label
133→                    }
134→                  </span>
135→                )}
136→              </div>
137→              <p className='h-6 text-xs text-muted-foreground'>
138→                {post.metadata.publishedAt}
139→              </p>
140→            </div>
141→          </Link>
142→        </BlurFade>
143→      ))}
144→
145→      {totalPages > 1 && (
146→        <BlurFade delay={BLUR_FADE_DELAY * 3}>
147→          <div className='flex items-center justify-center gap-2 mt-8 pt-4 border-t'>
148→            <button
149→              onClick={() => handlePageChange(currentPage - 1)}
150→              disabled={currentPage === 1}
151→              className='px-3 py-2 rounded-lg text-sm font-medium transition-colors disabled:opacity-50 disabled:cursor-not-allowed bg-secondary text-secondary-foreground hover:bg-secondary/80'>
152→              Previous
153→            </button>
154→
155→            <div className='flex items-center gap-1'>
156→              {getPageNumbers().map((page, index) =>
157→                page === 'ellipsis' ? (
158→                  <span key={`ellipsis-${index}`} className='px-2 text-muted-foreground'>
159→                    ...
160→                  </span>
161→                ) : (
162→                  <button
163→                    key={page}
164→                    onClick={() => handlePageChange(page)}
165→                    className={`w-9 h-9 rounded-lg text-sm font-medium transition-colors ${
166→                      currentPage === page
167→                        ? 'bg-primary text-primary-foreground'
168→                        : 'bg-secondary text-secondary-foreground hover:bg-secondary/80'
169→                    }`}>
170→                    {page}
171→                  </button>
172→                )
173→              )}
174→            </div>
175→
176→            <button
177→              onClick={() => handlePageChange(currentPage + 1)}
178→              disabled={currentPage === totalPages}
179→              className='px-3 py-2 rounded-lg text-sm font-medium transition-colors disabled:opacity-50 disabled:cursor-not-allowed bg-secondary text-secondary-foreground hover:bg-secondary/80'>
180→              Next
181→            </button>
182→          </div>
183→
184→          <p className='text-center text-sm text-muted-foreground mt-4'>
185→            {startIndex + 1}-{Math.min(startIndex + POSTS_PER_PAGE, filteredPosts.length)} of {filteredPosts.length} posts
186→          </p>
187→        </BlurFade>
188→      )}
189→    </section>
190→  );
191→}
