  1→import fs from 'fs';
  2→import matter from 'gray-matter';
  3→import path from 'path';
  4→import rehypePrettyCode from 'rehype-pretty-code';
  5→import rehypeStringify from 'rehype-stringify';
  6→import remarkGfm from 'remark-gfm';
  7→import remarkParse from 'remark-parse';
  8→import remarkRehype from 'remark-rehype';
  9→import { unified } from 'unified';
 10→
 11→export type Category = 'thoughts' | 'research' | 'economics' | 'philosophy' | 'investing';
 12→
 13→export type Metadata = {
 14→  title: string;
 15→  publishedAt: string;
 16→  summary: string;
 17→  image?: string;
 18→  category?: Category;
 19→};
 20→
 21→function getMDXFiles(dir: string) {
 22→  return fs.readdirSync(dir).filter((file) => path.extname(file) === '.mdx');
 23→}
 24→
 25→function generateId(text: string): string {
 26→  return text
 27→    .toLowerCase()
 28→    .replace(/[^\w\s\u4e00-\u9fff-]/g, '')
 29→    .replace(/\s+/g, '-');
 30→}
 31→
 32→function addHeadingIds(html: string): string {
 33→  return html.replace(
 34→    /<(h[1-6])>([^<]+)<\/h[1-6]>/g,
 35→    (_, tag, content) => {
 36→      const id = generateId(content);
 37→      return `<${tag} id="${id}">${content}</${tag}>`;
 38→    }
 39→  );
 40→}
 41→
 42→export async function markdownToHTML(markdown: string) {
 43→  const p = await unified()
 44→    .use(remarkParse)
 45→    .use(remarkGfm)
 46→    .use(remarkRehype)
 47→    .use(rehypePrettyCode, {
 48→      // https://rehype-pretty.pages.dev/#usage
 49→      theme: {
 50→        light: 'min-light',
 51→        dark: 'min-dark'
 52→      },
 53→      keepBackground: false
 54→    })
 55→    .use(rehypeStringify)
 56→    .process(markdown);
 57→
 58→  return addHeadingIds(p.toString());
 59→}
 60→
 61→export type TocItem = {
 62→  id: string;
 63→  text: string;
 64→  level: number;
 65→};
 66→
 67→function extractToc(markdown: string): TocItem[] {
 68→  const headingRegex = /^(#{1,6})\s+(.+)$/gm;
 69→  const toc: TocItem[] = [];
 70→  let match;
 71→
 72→  while ((match = headingRegex.exec(markdown)) !== null) {
 73→    const level = match[1].length;
 74→    const text = match[2].trim();
 75→    const id = text
 76→      .toLowerCase()
 77→      .replace(/[^\w\s\u4e00-\u9fff-]/g, '')
 78→      .replace(/\s+/g, '-');
 79→    toc.push({ id, text, level });
 80→  }
 81→
 82→  return toc;
 83→}
 84→
 85→export async function getPost(slug: string) {
 86→  const filePath = path.join('content', `${slug}.mdx`);
 87→  let source = fs.readFileSync(filePath, 'utf-8');
 88→  const { content: rawContent, data: metadata } = matter(source);
 89→  const content = await markdownToHTML(rawContent);
 90→  const toc = extractToc(rawContent);
 91→  return {
 92→    source: content,
 93→    metadata: metadata as Metadata,
 94→    slug,
 95→    toc
 96→  };
 97→}
 98→
 99→async function getAllPosts(dir: string) {
100→  let mdxFiles = getMDXFiles(dir);
101→  return Promise.all(
102→    mdxFiles.map(async (file) => {
103→      let slug = path.basename(file, path.extname(file));
104→      let { metadata, source } = await getPost(slug);
105→      return {
106→        metadata: metadata as Metadata,
107→        slug,
108→        source
109→      };
110→    })
111→  );
112→}
113→
114→export async function getBlogPosts() {
115→  return getAllPosts(path.join(process.cwd(), 'content'));
116→}
