 8→export function formatDate(date: string) {
 9→  let currentDate = new Date().getTime();
10→  if (!date.includes("T")) {
11→    date = `${date}T00:00:00`;
12→  }
13→  let targetDate = new Date(date).getTime();
14→  let timeDifference = Math.abs(currentDate - targetDate);
15→  let daysAgo = Math.floor(timeDifference / (1000 * 60 * 60 * 24));
16→
17→  let fullDate = new Date(date).toLocaleString("en-us", {
18→    month: "long",
19→    day: "numeric",
20→    year: "numeric",
21→  });
22→
23→  if (daysAgo < 1) {
24→    return "Today";
25→  } else if (daysAgo < 7) {
26→    return `${fullDate} (${daysAgo}d ago)`;
27→  } else if (daysAgo < 30) {
28→    const weeksAgo = Math.floor(daysAgo / 7);
29→    return `${fullDate} (${weeksAgo}w ago)`;
30→  } else if (daysAgo < 365) {
31→    const monthsAgo = Math.floor(daysAgo / 30);
32→    return `${fullDate} (${monthsAgo}mo ago)`;
33→  } else {
34→    const yearsAgo = Math.floor(daysAgo / 365);
35→    return `${fullDate} (${yearsAgo}y ago)`;
36→  }
