        {filteredTools.map((tool, id) => (
          <BlurFade delay={BLUR_FADE_DELAY * 2 + id * 0.05} key={tool.name}>
            <a
              href={tool.link}
              target='_blank'
              rel='noopener noreferrer'
              className='border rounded-lg p-4 hover:shadow-md transition-shadow block'>
