    10‚Üí
    11‚Üíconst CATEGORY_KEYWORDS: Record<string, string[]> = {
    12‚Üí  thoughts: ["Web3", "Blockchain", "Crypto Thoughts", "Opinion"],
    13‚Üí  research: ["Crypto Research", "Blockchain Analysis", "Investment Report", "DeFi Research"],
    14‚Üí  economics: ["Crypto Economics", "Token Economics", "Blockchain Economics", "Digital Assets"],
    15‚Üí  philosophy: ["Crypto Philosophy", "Decentralization", "Web3 Philosophy"],
