    18‚Üí
    19‚Üífunction resolveOgImageUrl(image: string | undefined, title: string) {
    20‚Üí  if (!image || image.trim().length === 0) {
    21‚Üí    return `${DATA.url}/og?title=${encodeURIComponent(title)}`;
    22‚Üí  }
    23‚Üí
    24‚Üí  if (image.startsWith("http://") || image.startsWith("https://")) {
    25‚Üí    return image;
    26‚Üí  }
    27‚Üí
    28‚Üí  if (image.startsWith("/")) {
    29‚Üí    return `${DATA.url}${image}`;
    30‚Üí  }
    31‚Üí
    32‚Üí  return `${DATA.url}/${image}`;
    33‚Üí}
    34‚Üí
    35‚Üífunction toIsoOrNow(value: string) {
    36‚Üí  const date = new Date(value);
    37‚Üí  if (Number.isNaN(date.getTime())) {
    38‚Üí    return new Date().toISOString();
    39‚Üí  }
    40‚Üí  return date.toISOString();
    41‚Üí}
    42‚Üí
    43‚Üíexport async function generateStaticParams() {
    44‚Üí  const posts = await getBlogPosts();
    45‚Üí  return posts.map((post) => ({ slug: post.slug }));
    46‚Üí}
    47‚Üí
    48‚Üíexport async function generateMetadata({
    49‚Üí  params,
    50‚Üí}: {
    51‚Üí  params: Promise<{
    52‚Üí    slug: string;
    53‚Üí  }>;
    54‚Üí}): Promise<Metadata | undefined> {
    55‚Üí  const { slug } = await params;
    56‚Üí  let post = await getPost(slug);
    57‚Üí  if (!post) {
    58‚Üí    return undefined;
    59‚Üí  }
    60‚Üí
    61‚Üí  let {
    62‚Üí    title,
    63‚Üí    publishedAt: publishedTime,
    64‚Üí    summary,
    65‚Üí    image,
    66‚Üí    category,
    67‚Üí  } = post.metadata;
    68‚Üí  const description = summary || `${title} - ${DATA.name}`;
    69‚Üí  let ogImage = resolveOgImageUrl(image, title);
    70‚Üí
    71‚Üí  const keywords = [
    72‚Üí    "Web3",
    73‚Üí    "Blockchain",
    74‚Üí    "Cryptocurrency",
    75‚Üí    "DeFi",
    76‚Üí    ...(category && CATEGORY_KEYWORDS[category] ? CATEGORY_KEYWORDS[category] : []),
    77‚Üí  ];
    78‚Üí
    79‚Üí  return {
    80‚Üí    title,
    81‚Üí    description,
    82‚Üí    keywords,
    83‚Üí    authors: [{ name: DATA.name, url: DATA.url }],
    84‚Üí    creator: DATA.name,
    85‚Üí    publisher: DATA.name,
    86‚Üí    alternates: {
    87‚Üí      canonical: `${DATA.url}/blog/${post.slug}`,
    88‚Üí    },
    89‚Üí    openGraph: {
    90‚Üí      title,
    91‚Üí      description,
    92‚Üí      type: "article",
    93‚Üí      publishedTime,
    94‚Üí      url: `${DATA.url}/blog/${post.slug}`,
    95‚Üí      siteName: DATA.name,
    96‚Üí      locale: "en_US",
    97‚Üí      authors: [DATA.name],
    98‚Üí      images: [
    99‚Üí        {
   100‚Üí          url: ogImage,
   101‚Üí          width: 1200,
   102‚Üí          height: 630,
   103‚Üí          alt: title,
   104‚Üí        },
   105‚Üí      ],
   106‚Üí    },
   107‚Üí    twitter: {
   108‚Üí      card: "summary_large_image",
   109‚Üí      title,
   110‚Üí      description,
   111‚Üí      creator: "@0xkkdemian",
   112‚Üí      images: [ogImage],
   113‚Üí    },
   114‚Üí    category: category || "Technology",
   115‚Üí  };
   116‚Üí}
   117‚Üí
   118‚Üíexport default async function Blog({
   119‚Üí  params,
   120‚Üí}: {
   121‚Üí  params: Promise<{
   122‚Üí    slug: string;
   123‚Üí  }>;
   124‚Üí}) {
   125‚Üí  const { slug } = await params;
   126‚Üí  let post = await getPost(slug);
