export default async function BlogPage() {
  const posts = await getBlogPosts();
  const sortedPosts = posts.sort(
    (a, b) =>
      new Date(b.metadata.publishedAt).getTime() -
      new Date(a.metadata.publishedAt).getTime()
  );

  const blogJsonLd = {
    '@context': 'https://schema.org',
    '@type': 'Blog',
