 1→import Image from 'next/image';
 2→import Link from 'next/link';
 3→import React from 'react';
 4→
 5→function Table({ data }: { data: { headers: string[]; rows: string[][] } }) {
 6→  let headers = data.headers.map((header, index) => (
 7→    <th key={index}>{header}</th>
 8→  ));
 9→  let rows = data.rows.map((row, index) => (
10→    <tr key={index}>
11→      {row.map((cell, cellIndex) => (
12→        <td key={cellIndex}>{cell}</td>
13→      ))}
14→    </tr>
15→  ));
16→
17→  return (
18→    <table>
19→      <thead>
20→        <tr>{headers}</tr>
21→      </thead>
22→      <tbody>{rows}</tbody>
23→    </table>
24→  );
25→}
26→
27→function CustomLink(props: any) {
28→  let href = props.href;
29→
30→  if (href.startsWith('/')) {
31→    return (
32→      <Link href={href} {...props}>
33→        {props.children}
34→      </Link>
35→    );
36→  }
37→
38→  if (href.startsWith('#')) {
39→    return <a {...props} />;
40→  }
41→
42→  return <a target='_blank' rel='noopener noreferrer' {...props} />;
43→}
44→
45→function RoundedImage(props: any) {
46→  return <Image alt={props.alt} className='rounded-lg' {...props} />;
47→}
48→
49→// This replaces rehype-slug
50→function slugify(str: string) {
51→  return str
52→    .toString()
53→    .toLowerCase()
54→    .trim() // Remove whitespace from both ends of a string
55→    .replace(/\s+/g, '-') // Replace spaces with -
56→    .replace(/&/g, '-and-') // Replace & with 'and'
57→    .replace(/[^\w\-]+/g, '') // Remove all non-word characters except for -
58→    .replace(/\-\-+/g, '-'); // Replace multiple - with single -
59→}
60→
61→function createHeading(level: number) {
62→  const Heading = ({ children }: { children: React.ReactNode }) => {
63→    let slug = slugify(children as string);
64→    return React.createElement(
65→      `h${level}`,
66→      { id: slug },
67→      [
68→        React.createElement('a', {
69→          href: `#${slug}`,
70→          key: `link-${slug}`,
71→          className: 'anchor'
72→        })
73→      ],
74→      children
75→    );
76→  };
77→  Heading.displayName = `Heading${level}`;
78→  return Heading;
79→}
80→
81→export const globalComponents = {
82→  h1: createHeading(1),
83→  h2: createHeading(2),
84→  h3: createHeading(3),
85→  h4: createHeading(4),
86→  h5: createHeading(5),
87→  h6: createHeading(6),
88→  Image: RoundedImage,
89→  a: CustomLink,
90→  Table
91→};
