 15→export async function generateMetadata({
 16→  params,
 17→}: {
 18→  params: Promise<{
 19→    slug: string;
 20→  }>;
 21→}): Promise<Metadata | undefined> {
 22→  const { slug } = await params;
 23→  let post = await getPost(slug);
 24→
 25→  let {
 26→    title,
 27→    publishedAt: publishedTime,
 28→    summary: description,
 29→    image,
 30→    category,
 31→  } = post.metadata;
 32→  let ogImage = image ? `${DATA.url}${image}` : `${DATA.url}/og?title=${title}`;
 33→
 34→  // Generate keywords based on category and content
 35→  const categoryKeywords: Record<string, string[]> = {
 36→    thoughts: ["Web3", "Blockchain", "Crypto Thoughts", "Opinion"],
 37→    research: ["Crypto Research", "Blockchain Analysis", "Investment Report", "DeFi Research"],
 38→    economics: ["Crypto Economics", "Token Economics", "Blockchain Economics", "Digital Assets"],
 39→    philosophy: ["Crypto Philosophy", "Decentralization", "Web3 Philosophy"],
 40→    investing: ["Crypto Investment", "DeFi Investing", "Digital Assets", "ETF", "Bitcoin", "Ethereum"],
 41→  };
 42→
 43→  const keywords = [
 44→    "Web3",
 45→    "Blockchain",
 46→    "Cryptocurrency",
 47→    "DeFi",
 48→    ...(category && categoryKeywords[category] ? categoryKeywords[category] : []),
 49→  ];
 50→
 51→  return {
 52→    title,
 53→    description,
 54→    keywords,
 55→    authors: [{ name: DATA.name, url: DATA.url }],
 56→    creator: DATA.name,
 57→    publisher: DATA.name,
 58→    alternates: {
 59→      canonical: `${DATA.url}/blog/${post.slug}`,
 60→    },
 61→    openGraph: {
 62→      title,
 63→      description,
 64→      type: "article",
 65→      publishedTime,
 66→      url: `${DATA.url}/blog/${post.slug}`,
 67→      siteName: DATA.name,
 68→      locale: "en_US",
 69→      authors: [DATA.name],
 70→      images: [
 71→        {
 72→          url: ogImage,
 73→          width: 1200,
 74→          height: 630,
 75→          alt: title,
 76→        },
 77→      ],
 78→    },
 79→    twitter: {
 80→      card: "summary_large_image",
 81→      title,
 82→      description,
 83→      creator: "@0xkkdemian",
 84→      images: [ogImage],
 85→    },
 86→    category: category || "Technology",
 87→  };
