103→
104→  const jsonLd = {
105→    "@context": "https://schema.org",
106→    "@type": "BlogPosting",
107→    headline: post.metadata.title,
108→    description: post.metadata.summary,
109→    image: post.metadata.image
110→      ? `${DATA.url}${post.metadata.image}`
111→      : `${DATA.url}/og?title=${post.metadata.title}`,
112→    datePublished: post.metadata.publishedAt,
113→    dateModified: post.metadata.publishedAt,
114→    author: {
115→      "@type": "Person",
116→      name: DATA.name,
117→      url: DATA.url,
118→      sameAs: [
119→        "https://github.com/chuhemiao",
120→        "https://x.com/0xkkdemian",
121→        "https://www.youtube.com/@kkdemian",
122→      ],
123→    },
124→    publisher: {
125→      "@type": "Person",
126→      name: DATA.name,
127→      url: DATA.url,
128→    },
129→    url: `${DATA.url}/blog/${post.slug}`,
130→    mainEntityOfPage: {
131→      "@type": "WebPage",
132→      "@id": `${DATA.url}/blog/${post.slug}`,
133→    },
134→    keywords: post.metadata.category
135→      ? [post.metadata.category, "Web3", "Blockchain", "Cryptocurrency", "DeFi"]
136→      : ["Web3", "Blockchain", "Cryptocurrency"],
137→    articleSection: post.metadata.category || "Technology",
